mapper.update:
update rk
set username = #{username,jdbcType=VARCHAR},
  rolename = #{rolename,jdbcType=VARCHAR},
  crypto_rolekey = #{cryptoRolekey,jdbcType=VARCHAR},
  crypto_rolesign = #{cryptoRolesign,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, username, rolename, crypto_rolekey, crypto_rolesign
from rk
where id = #{id,jdbcType=INTEGER}
